                <input 
                    type="password" 
                    name="password" 
                    placeholder="*******" 
                    value={password} 
                    onChange={(e) => setPassword(e.target.value)}/>
                <button type="submit">Edit Email</button>
            </form>
            <Link to="./profile"><button>Cancel</button></Link> */

export default withRouter(EditUserEmail);
